---------------------- STBase-- By Spacetech--------------------STGamemodes.Marquee = {}STGamemodes.Marquee.Count = {}STGamemodes.Marquee.Direction = {}STGamemodes.Marquee.LastThink = {}STGamemodes.Marquee.NextChange = CurTime()STGamemodes.Marquee.TopTexture = surface.GetTextureID("sassilization/hud/topbar_bg")STGamemodes.Marquee.BottomTexture = surface.GetTextureID("sassilization/hud/bottombar_bg")function STGamemodes.Marquee:Think()	for k,v in pairs(self.Count) do		self.Count[k] = v + (FrameTime() * 50 * (self.Direction[k] or 1))	endendhook.Add("Think", "STGamemodes.Marquee.Think", function() STGamemodes.Marquee:Think() end)surface.CreateFont("MenuLarge", {font="Verdana", size=15, weight=600, antialias=true})function STGamemodes.Marquee:Draw(id, x, y, tab, backnforth, center)	if(LocalPlayer():Alive() or table.Count(tab) == 0) then		return	end		if(!x) then		x = 0	end	if(!y) then		y = 0	end		if(self.Count[id] == nil) then		self.Count[id] = 0	end	if(self.Direction[id] == nil) then		self.Direction[id] = 1	end		local Text = ""	for k,v in pairs(tab) do		Text = Text.." - "..v	end	Text = string.sub(Text, 3)		surface.SetFont("MenuLarge")		local width, height = surface.GetTextSize(Text)	local scrnwidth = ScrW()		local count = self.Count[id]	local direction = self.Direction[id]		if(center) then		count = ((ScrW() - x) / 2) - (width / 2)	elseif(backnforth) then		if((x + count + width) >= scrnwidth) then			self.Direction[id] = -1		end		if(count < 1) then			self.Direction[id] = 1		end		if(count < x) then			self.Count[id] = 0			count = 0		end	else		if(count >= scrnwidth) then			count = width * -1			self.Count[id] = count		end		if(count < 0 and x > 0) then			Text = string.sub(Text, (count + width + 1) * -1)			count = 1		end	end		if(y == 0) then		surface.SetTexture(self.TopTexture)	else		surface.SetTexture(self.BottomTexture)	end	surface.SetDrawColor(255, 255, 255, 255)	surface.DrawTexturedRect(x, y, ScrW() - x, 32)	//	surface.SetDrawColor(100, 100, 100, 180)	//surface.DrawOutlinedRect(x - 1, y - 1, ScrW() - x + 2, 32 + 2)		surface.SetTextPos(x + count, y + 7)	surface.SetTextColor(255, 255, 255, 255)	surface.DrawText(Text)end