---------------------- STBase-- By Spacetech--------------------GM.RunnerWeapon = "weapon_crowbar"STGamemodes:EnablePitchCheck(true)--[[ -- Partially lowers the platforms into the lava so that there's enough space for players to crouch underneath the gates.hook.Add("OnRoundChange", "KeyValueChanger", function()	for k,v in pairs(ents.FindByClass("func_tracktrain")) do		STGamemodes.KeyValues:AddChange( v, "height", "-8" )	endend) ]]-- Teleporter for the first gate on the platform section.STGamemodes.TouchEvents:Setup(Vector(3025, 3312, -66), Vector(3113, 3314, 17.0754), function(ply)	ply:SetPos(Vector(3033.0313, 3355.0313, 33.2062))	ply:SetEyeAngles(Angle(60, 45, 0))end)-- Stops players from lunging forward once they step through the first teleporter.STGamemodes.TouchEvents:Setup(Vector(3033.0313, 3355.0313, 33.2062), 16, function(ply)	ply:SetLocalVelocity(Vector(0, 0, 0))end)-- Teleporter for the second gate on the platform section.STGamemodes.TouchEvents:Setup(Vector(3277, 4144.99, -66), Vector(3284, 4056.99, 9.42915), function(ply)	ply:SetPos(Vector(3320.0212, 4143.9688, 23.7520))	ply:SetEyeAngles(Angle(60, -45, 0))end)-- Stops players from lunging forward once they step through the second teleporter.STGamemodes.TouchEvents:Setup(Vector(3320.0212, 4143.9688, 23.7520), 16, function(ply)	ply:SetLocalVelocity(Vector(0, 0, 0))end)